mapper.select:
select
,t.category_id, tc.category_name
from textbook t
left join textbook_category tc on tc.category_id = t.category_id

     and textbook_id = #{textbookId}
     and textbook_name like concat('%', #{textbookName}, '%')
     and tc.category_name= #{categoryName}
     and order_status= #{orderStatus}

order by textbook_id asc
