mapper.select:
select
,u.real_name, d.department_name
from order_form o
left join user u on o.applicant_id = u.user_id
left join department d on o.department_id = d.department_id

     and textbook_name like concat('%', #{textbookName}, '%')
     and cancel_status= #{cancelStatus}
     and application_status = #{applicationStatus}
     and applicant_id = #{applicantId}
     and d.department_id = #{departmentId}

order by user_id asc
